import React, { useEffect, useState } from 'react';
import ExperienceSection from './ExperienceSection';
import { useObserver } from '../../hooks/useObserver';
import { themes } from '../../utils/themesAbount';
import { skills } from '../../data/AbountMe/skills';
import BackgroundElements from './BackgrountdElements';
import AboutTitle from './AbountTitle'; 
import CardInfo from './CardInfo';
import PersonDescription from './PersonDescription';
import Motivation from './Motivation';
import SkillsSection from './SkillsSection';
import CallToAction from './CallToAction';

const AboutSection = ({ theme = 'light' }) => {

   const isVisible = useObserver('about');
   const currentTheme = themes[theme];
 
  return (
    <section id="about" className={`py-20 ${currentTheme.bg} ${currentTheme.text} relative overflow-hidden`}>
      {/* Elementos decorativos de fondo */}
      <BackgroundElements/>

      <div className="max-w-7xl mx-auto px-4 relative z-10">
        
        {/* Título principal */}
       <AboutTitle currentTheme={currentTheme} isVisible={isVisible} />

        <div className="grid lg:grid-cols-2 gap-12 items-center">
          
          {/* Imagen y información personal */}
          <CardInfo isVisible={isVisible} currentTheme={currentTheme} />

          {/* Contenido principal */}
          <div className={`space-y-8 ${isVisible ? 'animate-slide-in-right' : 'opacity-0'}`}>
            
            {/* Historia personal */}
           <PersonDescription currentTheme={currentTheme} />

            {/* Lo que me motiva */}
            <Motivation currentTheme={currentTheme} />
          </div>
        </div>

        <ExperienceSection/>
        
        {/* Skills principales */}
        <SkillsSection currentTheme={currentTheme} isVisible={isVisible} skills={skills} />

        {/* Call to action */}
        <CallToAction currentTheme={currentTheme} isVisible={isVisible} />

      </div>
    </section>
  );
};

// CSS adicional que necesitas agregar a tu archivo de estilos
const additionalCSS = `
@keyframes fade-in {
  from { opacity: 0; transform: translateY(20px); }
  to { opacity: 1; transform: translateY(0); }
}

@keyframes slide-in-left {
  from { opacity: 0; transform: translateX(-50px); }
  to { opacity: 1; transform: translateX(0); }
}

@keyframes slide-in-right {
  from { opacity: 0; transform: translateX(50px); }
  to { opacity: 1; transform: translateX(0); }
}

@keyframes fade-in-up {
  from { opacity: 0; transform: translateY(30px); }
  to { opacity: 1; transform: translateY(0); }
}

.animate-fade-in {
  animation: fade-in 0.8s ease-out forwards;
}

.animate-slide-in-left {
  animation: slide-in-left 0.8s ease-out forwards;
}

.animate-slide-in-right {
  animation: slide-in-right 0.8s ease-out forwards;
}

.animate-fade-in-up {
  animation: fade-in-up 0.8s ease-out forwards;
}
`;

export default AboutSection;
