import React from 'react';

const HeroSection = ({ gradientType = 1 }) => {
  // Definimos los diferentes gradientes como opciones
  const gradients = [
    'bg-gradient-to-br from-indigo-900 to-purple-800', // Original
    //'bg-gradient-to-br from-blue-900 to-blue-500',     // Azul eléctrico
    'bg-gradient-to-br from-purple-900 via-pink-600 to-blue-500', // Morado neón
    'bg-gradient-to-br from-gray-900 via-purple-900 to-gray-800'  // Oscuro moderno
  ];

  // Paletas de colores alternativas para acentos
  const accentColors = [
    //'text-yellow-300', // Original
    'text-emerald-400',
    'text-pink-500', 
    'text-cyan-400',
    'text-amber-400',
    'text-violet-500'
  ];

  return (
    <section className={`min-h-screen flex flex-col justify-center items-center ${gradients[gradientType]} text-white px-4`}>
      <div className="max-w-4xl mx-auto text-center">
        <h1 className="text-5xl md:text-7xl font-bold mb-6 animate-fade-in">
          Hola, soy <span className={accentColors[gradientType]}>[David Marin]</span>
        </h1>
        <h2 className="text-2xl md:text-3xl font-semibold mb-8 animate-fade-in delay-100">
          Desarrollador Full-Stack Móvil & Web
        </h2>
        <p className="text-lg md:text-xl mb-12 max-w-2xl mx-auto animate-fade-in delay-200">
          Creo experiencias digitales impactantes que funcionan perfectamente en cualquier dispositivo.
        </p>
        <div className="flex gap-4 justify-center animate-fade-in delay-300">
          <button className={`px-8 py-3 ${gradientType === 0 ? 'bg-yellow-400' : 'bg-white'} text-gray-900 font-bold rounded-full hover:bg-opacity-90 transition-colors shadow-lg`}>
            Ver Proyectos
          </button>
          <button className="px-8 py-3 border-2 border-white text-white font-bold rounded-full hover:bg-white hover:text-gray-900 transition-colors">
            Contacto
          </button>
        </div>
      </div>
      <div className="absolute bottom-10 animate-bounce">
        <svg className="w-8 h-8 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 14l-7 7m0 0l-7-7m7 7V3" />
        </svg>
      </div>
    </section>
  );
};

export default HeroSection;

