import React, { useEffect, useState } from 'react';
import BackgroundElements from './BackgroundElements'; 
import HeroTitle from './HeroTitle';
import TypewriterSubtitle from './TypewriterSubtitle';
import HeroDescription from './HeroDescription';
import HeroButtons from './HeroButtons';
import SocialLinks from './SocialLinks';
import ScrollArrow from './ScrollArrow';
import {gradients, accentColors} from '../../utils/gradientConfig';
import { scrollToSection } from '../../utils/navigationHelpers';

const HeroSection = ({ gradientType = 1 }) => {
  // State para la animación de texto
  const [typedText, setTypedText] = useState('');
  const fullText = 'Desarrollador Full-Stack Móvil & Web';
  const [isTypingComplete, setIsTypingComplete] = useState(false);
  
  const heroData = {
    name: 'David Marin',
    subtitle: 'Desarrollador Full-Stack Móvil & Web',
    description: 'Creo experiencias digitales impactantes que funcionan perfectamente en cualquier dispositivo, combinando diseño atractivo con tecnologías modernas para impulsar tu presencia en línea.',
    cvUrl: 'https://drive.google.com/file/d/1c77b6Orx6sYT8FnxQIqi7nBB9d7NrFsf/view'
  };

    const handleContactClick = () => scrollToSection('contact');
  const handleScrollDown = () => scrollToSection('skills');

  
  // Efecto de escritura
  useEffect(() => {
    if (typedText.length < fullText.length) {
      const timeout = setTimeout(() => {
        setTypedText(fullText.slice(0, typedText.length + 1));
      }, 70); // Velocidad de escritura

      return () => clearTimeout(timeout);
    } else {
      setIsTypingComplete(true);
    }
  }, [typedText, fullText]);

  // Función para el scroll suave hacia abajo
  const scrollToNextSection = () => {
    const skillsSection = document.getElementById('skills');
    if (skillsSection) {
      skillsSection.scrollIntoView({ behavior: 'smooth' });
    }
  };

  // Función para la navegación a proyectos
  const navigateToProjects = () => {
    const projectsSection = document.getElementById('projects');
    if (projectsSection) {
      projectsSection.scrollIntoView({ behavior: 'smooth' });
    }
  };

  // Función para la navegación a contacto
  const navigateToContact = () => {
    const contactSection = document.getElementById('contact');
    if (contactSection) {
      contactSection.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <section className={`min-h-screen flex flex-col justify-center items-center ${gradients[gradientType]} text-white px-4 relative overflow-hidden`}>
      {/* Elementos de fondo para efecto visual */}
      <BackgroundElements />

      <div className="max-w-4xl mx-auto text-center z-10">
        <HeroTitle
        name={heroData.name} 
        accentColor={accentColors[gradientType]}
        />
        <TypewriterSubtitle text={heroData.subtitle}/>

        <HeroDescription description={heroData.description} />

         <HeroButtons
         gradientType={gradientType}
         onContactClick={handleContactClick}
         cvUrl={heroData.cvUrl}
        /> 
        

        {/* Redes sociales */}
        <div className="mt-12 flex justify-center space-x-6 animate-fade-in delay-400">
          <a href="https://github.com/davidmarint" target="_blank" rel="noopener noreferrer" className="text-white hover:text-gray-300 transition-colors">
            <svg className="w-6 h-6" fill="currentColor" viewBox="0 0 24 24" aria-hidden="true">
              <path fillRule="evenodd" d="M12 2C6.477 2 2 6.484 2 12.017c0 4.425 2.865 8.18 6.839 9.504.5.092.682-.217.682-.483 0-.237-.008-.868-.013-1.703-2.782.605-3.369-1.343-3.369-1.343-.454-1.158-1.11-1.466-1.11-1.466-.908-.62.069-.608.069-.608 1.003.07 1.531 1.032 1.531 1.032.892 1.53 2.341 1.088 2.91.832.092-.647.35-1.088.636-1.338-2.22-.253-4.555-1.113-4.555-4.951 0-1.093.39-1.988 1.029-2.688-.103-.253-.446-1.272.098-2.65 0 0 .84-.27 2.75 1.026A9.564 9.564 0 0112 6.844c.85.004 1.705.115 2.504.337 1.909-1.296 2.747-1.027 2.747-1.027.546 1.379.202 2.398.1 2.651.64.7 1.028 1.595 1.028 2.688 0 3.848-2.339 4.695-4.566 4.943.359.309.678.92.678 1.855 0 1.338-.012 2.419-.012 2.747 0 .268.18.58.688.482A10.019 10.019 0 0022 12.017C22 6.484 17.522 2 12 2z" clipRule="evenodd" />
            </svg>
          </a>
          <a href="https://linkedin.com/in/michael-david-marin-tapiero-509a95335" target="_blank" rel="noopener noreferrer" className="text-white hover:text-gray-300 transition-colors">
            <svg className="w-6 h-6" fill="currentColor" viewBox="0 0 24 24" aria-hidden="true">
              <path d="M20.447 20.452h-3.554v-5.569c0-1.328-.027-3.037-1.852-3.037-1.853 0-2.136 1.445-2.136 2.939v5.667H9.351V9h3.414v1.561h.046c.477-.9 1.637-1.85 3.37-1.85 3.601 0 4.267 2.37 4.267 5.455v6.286zM5.337 7.433c-1.144 0-2.063-.926-2.063-2.065 0-1.138.92-2.063 2.063-2.063 1.14 0 2.064.925 2.064 2.063 0 1.139-.925 2.065-2.064 2.065zm1.782 13.019H3.555V9h3.564v11.452zM22.225 0H1.771C.792 0 0 .774 0 1.729v20.542C0 23.227.792 24 1.771 24h20.451C23.2 24 24 23.227 24 22.271V1.729C24 .774 23.2 0 22.222 0h.003z" />
            </svg>
          </a>
        </div>
      </div>

      {/* Flecha animada para scroll */}
      <button
        onClick={scrollToNextSection}
        className="absolute bottom-10 animate-bounce cursor-pointer focus:outline-none"
        aria-label="Scroll hacia abajo"
      >
        <svg className="w-8 h-8 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 14l-7 7m0 0l-7-7m7 7V3" />
        </svg>
      </button>
    </section>
  );
};

export default HeroSection;
